mapper.select:
select id , if(sex = '1', '男', '女'), name, password , phone , status , create_time as createTime, update_time as updateTime, create_user as createUser, update_user as updateUser, id_number as idNumber, (select count(*) from employee
name like concat('%', #{name},'%')
) count from employee
name like concat('%', #{name},'%')
limit #{start}, #{pageSize}
