mapper.delete:
delete from dish




            id in

                #{item}

            and store_id = #{storeId}




        -- #如果没有提供ID，则不执行删除操作

mapper.select:
select t1.id, t1.name, t2.name as categoryName, t1.price, t1.status, t1.category_id as categoryId, t1.create_time as createTime, t1. create_user as createUser, t1. description, t1. image, t1. update_time as updateTime, t1. update_user as updateUser from dish t1 inner join category as t2 on t1.category_id = t2.id
and t1.name like CONCAT('%', #{name}, '%')
and t1.category_id = #{categoryId}
and t1.status = #{status}
and t1.store_id = #{storeId}
